import { Component} from '@angular/core';
import { Router} from '@angular/router';
import { GuardarDocumentoService } from '../../services/guardar-documento.service';
import { FormGroup , FormBuilder } from '@angular/forms';
import { DateFormatPipePipe } from '../../pipes/date-format-pipe.pipe';
import { DatePipe } from '@angular/common';
declare let jsPDF;

@Component({
  selector: 'app-asig-sinodales',
  templateUrl: './asig-sinodales.component.html',
  styleUrls: ['./asig-sinodales.component.css']
})
export class AsigSinodalesComponent {

  formulario: FormGroup;
  formulario2: FormGroup;
  private message: string ='';
  private campoFecha:any;
  private folio: string;
  private tipo: string;
  private categoria: string;
  private date_formattedPDF1: any;
  private date_formattedPDF2: any;
  private date_formattedBD: any;
  private time_formattedBD: any;
  private campoFecha2:any;

  constructor(private fb: FormBuilder,
    private route: Router,
    private saveDocService: GuardarDocumentoService)
    {
    this.folio = "";
    this.tipo = "Asignación de Sinodales";
    this.categoria = "Titulación";
    this.campoFecha = new Date();
    this.campoFecha2 = new Date();

    this.formulario = fb.group({
      jefeSistemas:'',
      jefeDivision:'',
      tipoTitulacion:'',
      nombreAlumno:'',
      carrera:''
    })
    this.formulario2 = fb.group({
      nombrePresidente:'',
      gradoPresidente:'',
      cedulaPresidente:'',
      nombreSecretario:'',
      gradoSecretario:'',
      cedulaSecretario:'',
      nombreVocal:'',
      gradoVocal:'',
      cedulaVocal:'',
      nombreSuplente:'',
      gradoSuplente:'',
      cedulaSuplente:'',
      departamento: 'Sistemas y computación'
    })
  }
  public generarPDF() {
    this.date_formattedPDF1 = new DateFormatPipePipe().transform(this.campoFecha);
    this.date_formattedBD = new DateFormatPipePipe().transformDateBD(this.campoFecha);
    this.time_formattedBD = new DateFormatPipePipe().transformTimeBD(this.campoFecha);
    this.date_formattedPDF2 = new DateFormatPipePipe().transform(this.campoFecha2);

    var encabezado = 'data:image/jpeg;base64,';
    encabezado+='';
    encabezado+='';
    encabezado+='';

    var doc = new jsPDF();

    doc.addImage(encabezado, 'jpeg', 20, 10, 170, 20);

    doc.setFont('helvetica');
    doc.setFontSize(10);
    doc.text(115,45, 'H.H. Cuautla. Mor.,_____________________');
    doc.text(148,45,this.date_formattedPDF1);

    doc.setFontType('bold');
    doc.text(121,50, 'OFICIO NUM. ITC-SA-');
    doc.setFontType('normal');
    doc.text(158,50,'______________ ');
    doc.text(160,50, this.folio);

    doc.text(135,55, 'Asunto: Asignación de Sinodales');

    doc.setFontType('bold');
    doc.text(24,70,this.formulario.value.jefeDivision.toLocaleUpperCase());
    doc.setFontSize(10);
    doc.text(24,75, 'JEFE DE DIVISIÓN DE ESTUDIOS PROFESIONALES');

    doc.setFontType('normal');
    doc.text(24,80, 'P R E S E N T E ');
    doc.text(20,85, `
    Por este medio  informo a usted la asignación de sinodales  para el Acto de Recepción Profesional por la
    opción  “TITULACIÓN ____________________”  del C. ____________________ egresado de la carrera
    de __________________________, que se llevará a cabo el día _______________________ en la sala
    de titulación, de acuerdo a la siguiente asignación: `);

    doc.text(65,93, this.formulario.value.tipoTitulacion);
    doc.text(112,93, this.formulario.value.nombreAlumno);
    doc.text(35,97, this.formulario.value.carrera);
    doc.text(135,97,this.date_formattedPDF2);


    doc.text(20,110, `
    Presidente




    Secretario




    Vocal




    Suplente




    `);
    doc.text(50,115, this.formulario2.value.nombrePresidente);
    doc.text(50,120, this.formulario2.value.gradoPresidente);
    doc.text(50,125, this.formulario2.value.cedulaPresidente);

    doc.text(50,135, this.formulario2.value.nombreSecretario);
    doc.text(50,140, this.formulario2.value.gradoSecretario);
    doc.text(50,145, this.formulario2.value.cedulaSecretario);

    doc.text(50,155, this.formulario2.value.nombreVocal);
    doc.text(50,160, this.formulario2.value.gradoVocal);
    doc.text(50,165, this.formulario2.value.cedulaVocal);

    doc.text(50,175, this.formulario2.value.nombreSuplente);
    doc.text(50,180, this.formulario2.value.gradoSuplente);
    doc.text(50,185, this.formulario2.value.cedulaSuplente);


    doc.text(24,200, 'Sin más por el momento, quedo de usted para cualquier aclaración.');

    doc.setFontType('bold');
    doc.text(24,210, 'A T E N T A M E N T E');

    doc.setFontSize(8);
    doc.text(24,215,'\"EN SU IDEAL DE SERVICIO RESIDE LA GRANDEZA DEL HOMBRE\"');

    doc.setFontType('bold');
    doc.setFontSize(10);
    doc.text(20,225, `



    \tNombre y firma
    JEFE DEL DEPARTAMENTO DE `);

    doc.text(24,236, this.formulario.value.jefeSistemas.toLocaleUpperCase());
    doc.text(80,245, this.formulario2.value.departamento.toLocaleUpperCase());

    doc.setFontType('normal');
    doc.setFontSize(8);
    doc.text(24,255, 'C.p. Archivo');
    doc.save('AsignacionSinodales.pdf');

    this.message = this.saveDocService.guardarDocumento(
      this.folio, this.tipo, this.categoria,
      this.date_formattedBD, this.time_formattedBD);

    console.log(this.message);
  }

  regresarPag(){
    this.route.navigate(['/login']);
  }

}
