import { Component} from '@angular/core';
import { Router} from '@angular/router';
import { GuardarDocumentoService } from '../../services/guardar-documento.service';
import { FormGroup , FormBuilder } from '@angular/forms';
import { DateFormatPipePipe } from '../../pipes/date-format-pipe.pipe';
import { DatePipe } from '@angular/common';
declare let jsPDF;

@Component({
  selector: 'app-solicitud-mantenimiento',
  templateUrl: './solicitud-mantenimiento.component.html',
  styleUrls: ['./solicitud-mantenimiento.component.css']
})
export class SolicitudMantenimientoComponent {
  formulario: FormGroup;
  private date_formattedPDF: any;
  private message: string ='';
  private campoFecha:any;
  private folio: string;
  private tipo: string;
  private categoria: string;
  private date_formattedBD: any;
  private time_formattedBD: any;
  constructor(private fb: FormBuilder, private route: Router,
    private saveDocService: GuardarDocumentoService){

    this.folio = "";
    this.tipo ="Formato de Solicitud de Mantenimineto";
    this.categoria = "Solicitudes";

    this.campoFecha = new Date();
    this.formulario = fb.group({
      mantenimientoEquipo:'',
      centroComputo:'',
      departamento:'',
      nombreSolicitante: '',
      descripcionServicio:'',
      liberacionServicio:''
    })
  }
    public generarPDF() {
      this.date_formattedPDF = new DateFormatPipePipe().transform(this.campoFecha);
      this.date_formattedBD = new DateFormatPipePipe().transformDateBD(this.campoFecha);
      this.time_formattedBD = new DateFormatPipePipe().transformTimeBD(this.campoFecha);
      var encabezado = 'data:image/jpeg;base64,';
      encabezado+='';
      encabezado+='';
      var doc = new jsPDF;

      doc.addImage(encabezado, 'jpeg', 20, 10, 170, 20);
      doc.setFont('helvetica');
      doc.setFontSize(12);

      doc.setFontType('bold');
      doc.text(65,40, 'SOLICITUD DE MANTENIMIENTO ');

      doc.setFontType('normal');
      doc.setFontSize(10);
      doc.text(20,55, `
      Mantenimiento del equipo: _________________________  Centro de Cómputo: ______________________
      Fecha de solicitud: ________________________________________ Folio: _________________________
      Área Solicitante: ________________________________________________________________________
      Nombre y Firma del Solicitante: ____________________________________________________________
      `);
      doc.text(75,59, this.formulario.value.mantenimientoEquipo);
      doc.text(155,59, this.formulario.value.centroComputo);

      doc.text(60,63, this.date_formattedPDF);
      doc.text(153,63, this.folio);
      doc.text(55,67, this.formulario.value.departamento);
      doc.text(77,71, this.formulario.value.nombreSolicitante.toLocaleUpperCase());

      doc.setFontType('bold');
      doc.text(24,80, 'Descripción del servicio solicitado:');

      doc.setFontType('normal');
      doc.rect(24, 85, 167, 100);
      doc.text(30,95, this.formulario.value.descripcionServicio);

      doc.rect(27, 190, 160, 30);

      doc.text(80,204, this.formulario.value.liberacionServicio.toLocaleUpperCase());

      doc.setLineWidth(0.35);
      doc.line(27, 205, 187, 205);
      doc.text(60,210, 'Nombre, Fecha y Firma de liberación del servicio');

      doc.setFontType('normal');
      doc.setFontSize(8);
      doc.text(24,230, 'C.p. Area Solicitante');

      var pie ='data:image/jpeg;base64,';
      pie+='';
      pie+='';

      doc.addImage(pie, 'jpeg', 20, 265, 150, 25);
      doc.save('SolicitudMantenimiento.pdf');
      this.message = this.saveDocService.guardarDocumento(
        this.folio, this.tipo, this.categoria,
        this.date_formattedBD, this.time_formattedBD);
      console.log(this.message);
    }
    regresarPag(){
      this.route.navigate(['/login']);
    }
}
